import axios from 'axios';



const api = (axios) => {
  axios.defaults.withCredentials = true;
  api = axios.create({
    baseURL: 'http://localhost:3000',
    timeout: 10000,
    withCredentials: true,
    headers: {
      'Accept': 'application/json',
      'Content-Type': 'application/json',
    }
  });
  return api
};

export default api(axios);
