import styles from '../styles/Home.module.css';
import Link from 'next/link';
import Layout from '../src/components/Layout';
import React, { useState } from 'react';
import { API_URL } from '../src/constants';

export default function Login() {
  const [formData, setFormData] = useState({
    email: '',
    password: ''
  });

  const { email, password } = formData;

  const handleSubmit = async (e) => {
    e.preventDefault();

    const response = await fetch(`${API_URL}/login`, {
      method: 'POST',
      headers: {
        'Content-Type': 'application/json'
      },
      referrerPolicy: 'no-referrer',
      body: JSON.stringify(formData)
    });
    const res = await response.json(); // parses JSON response into native JavaScript objects
    console.log(response.body)
  }

  const handleChange = (e) => {
    setFormData({
      ...formData,
      [e.target.name]: e.target.value,
    })
  }

  return (
    <Layout>
      <>
        <h1 className={styles.title}>
          Login page ðŸ¥°
        </h1>
        <div className={styles["buttons-container"]}>
          <Link href="/"><a className={styles.button}>Home</a></Link>
        </div>
        <form className={styles['login-form']} onSubmit={handleSubmit}>
          <input name="email" className={styles.input} type="email" value={email} onChange={handleChange} />
          <input name="password" className={styles.input} type="password" value={password} onChange={handleChange} />
          <button type="submit" className={styles.button}>Submit</button>
        </form>
      </>
    </Layout>
  )
}
